INSERT INTO `viajes` (`id`, `titulo`, `descripcion`, `fecha_inicio`, `fecha_fin`, `precio`, `destacado`, `tipo`, `plazas`, `url_imagen`) VALUES (6, 'Colombia', 'Colombia es uno de esos países que han sabido transformarse. De no ser aptos para viajar, se han convertido en uno de los referentes de América. Y es que Colombia tiene mucho que ofrecer: sus animadas capitales, la belleza de su zona cafetera, sus pueblos coloniales y sus idílicas playas.', '2026-02-14', '2026-03-01', 1700, 1, 'Grupo', 50, 'https://blog.centraldereservas.com/wp-content/uploads/2022/01/Que-ver-en-Colombia.jpg');
